//return true if unique frequencies

import java.util.*;

public class uniqOccurances {
    public static void main(String[] args) {
        Scanner sc=new Scanner(System.in);
        int n=sc.nextInt();
        int[] a=new int[n];
        int local=0;
        boolean flag=true;
        ArrayList<Integer> al=new ArrayList<Integer>();
        for(int i=0;i<n;i++){
            a[i]=sc.nextInt();
        }
        System.out.println(Arrays.toString(a));
        Arrays.sort(a);
        for(int i=0;i<a.length;i=i+local){
            local=0;
            for(int j=i;j<n;j++){
                if(a[j]==a[i]){
                    local=local+1;
                }
                else{
                    break;
                }
            }
            al.add(local);
        }
        Collections.sort(al);
        for(int i=0;i<al.size()-1;i++){
            if(al.get(i)==al.get(i+1)){
                flag=false;
                break;
            }
        }
        System.out.println(flag);
    }
}

